(4,27,'base-gateway-dev.yml','DEFAULT_GROUP','','security:\n  encode:\n    # 前端密码密钥，必须16位\n    key: \'1234567891234567\'\n\n# 不校验验证码终端\nignore:\n  clients:\n    - swagger\n  swagger-providers:\n    - ${AUTH-HOST:base-auth}\n\nspring:\n  cloud:\n    gateway:\n      # 路由配置\n      routes:\n      # base-auth\n      - id: base-auth\n        predicates:\n        - name: Path\n          args: \n            _genkey_0: /auth/**\n        filters:\n        - name: ValidateCodeGatewayFilter\n          args: {}\n        - name: PasswordDecoderFilter\n          args: {}\n        uri: lb://base-auth\n        order: 0\n      # base-upms-admin\n      - id: base-upms-admin\n        predicates:\n        - name: Path\n          args: \n            _genkey_0: /admin/**\n        filters:\n        - name: RequestRateLimiter\n          args: \n            # 限流策略\n            key-resolver: \'#{@remoteAddrKeyResolver}\'\n            # 令牌桶每秒填充率\n            redis-rate-limiter.burstCapacity: 20\n             # 令牌桶容量\n            redis-rate-limiter.replenishRate: 10\n        # 熔断\n        - name: Hystrix\n          args: \n            fallbackUri: \'forward:/fallback\'\n            name: default\n        uri: lb://base-upms-admin\n        order: 0\n      # base-codegen\n      - id: base-codegen\n        predicates:\n        - name: Path\n          args: \n            _genkey_0: /gen/**\n        filters: []\n        uri: lb://base-codegen\n        order: 0\n      # base-weixin-mp\n      - id: base-weixin-mp\n        predicates:\n        - name: Path\n          args: \n            _genkey_0: /wxmp/**\n        filters: \n        uri: lb://base-weixin-mp\n        order: 0\n','c63ecf1eb09ee8f82cfff180e60ffd87','2010-05-05 00:00:00','2019-07-30 08:40:16',NULL,'192.168.1.117','U',''),
